import React, { useState } from 'react';

function App() {
  const [tarefa, setTarefa] = useState([]);
  const [novaTarefa, setNovaTarefa] = useState('');

  const handleAddTask = () => {
    if (novaTarefa.trim() !== '') {
      setTarefa([...tarefa, { name: novaTarefa, completed: false }]);
      setNovaTarefa('');
    }
  };

  const handleToggleComplete = (index) => {
    const updatedtarefa = [...tarefa];
    updatedtarefa[index].completed = !updatedtarefa[index].completed;
    setTarefa(updatedtarefa);
  };

  const handleRemoveTask = (index) => {
    const updatedtarefa = [...tarefa];
    updatedtarefa.splice(index, 1);
    setTarefa(updatedtarefa);
  };

  return (
    <div>
      <h1>Lista de Tarefas</h1>
      <div>
        <input
          type="text"
          placeholder="Digite uma nova tarefa"
          value={novaTarefa}
          onChange={(e) => setNovaTarefa(e.target.value)}
        />
        <button onClick={handleAddTask}>Adicionar</button>
      </div>
      <ul>
        {tarefa.map((task, index) => (
          <li key={index}>
            <span
              style={{
                textDecoration: task.completed ? 'line-through' : 'none',
                color: task.completed ? 'green' : 'red',
              }}
            >
              {task.name}
            </span>
            <button onClick={() => handleToggleComplete(index)}>
              {task.completed ? 'Desfazer' : 'Concluir'}
            </button>
            <button onClick={() => handleRemoveTask(index)}>Remover</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
